mapper.update:
    update tb_apply
    set 	pname = #{pname}
			,relation = #{relation}
			,pbirth = #{pbirth}
			,addr = #{addr}
			,phone = #{phone}
			,cname = #{cname}
			,cgender = #{cgender}
			,cbirth = #{cbirth}
			,apptype = #{apptype}
	where ano = #{ano}

	 	{
		 	update tb_apply
		 	set cunicode = #{cunicode}
		 		,cclass = #{cclass}
		 		,enter = to_date(#{enter})
		 		,graduation = to_date(#{graduation})
	 		where ano = #{ano};


	 		update tb_child

	 		insert into tb_child(cunicode, className, cname, cbirth)
	 		values(#{cunicode}, #{cclass}, #{cname}, #{cbirth});
 		}
