mapper.update:
 update tb_apply
 set    pname = #{pname}
      ,relation = #{relation}
      ,pbirth = #{pbirth}
      ,addr = #{addr}
      ,phone = #{phone}
      ,useremail = #{useremail}
      ,cname = #{cname}
      ,cgender = #{cgender}
      ,cbirth = #{cbirth}
      ,apptype = #{apptype}
      ,appstate = #{appstate}
      ,pw = #{pw}
where ano = #{ano}

 {call
 	declare
 	begin
		insert into tb_child(cunicode, classname, cgender, cname, cbirth, enter, graduation)
		values(#{cunicode}, #{cclass}, #{cgender}, #{cname}, #{cbirth}, null, null);

		update tb_apply
		set		cunicode = #{cunicode}
				,cclass = #{cclass}
				,appstate = '승인완료'
		where ano = #{ano};
	end
 }
