mapper.insert:

	select seq_tb_apply.nextval from dual


insert into tb_apply (ano
						,pname
						,relation
						,pbirth
						,addr
						,phone
						,adddate
						,cname
						,cgender
						,cbirth
						,apptype
						,appstate
						,cunicode
						,cclass
						,enter
						,graduation
						,pw)
values (#{ano}
		,#{pname}
		,#{relation}
		,#{pbirth}
		,#{addr}
		,#{phone}
		,sysdate
		,#{cname}
		,#{cgender}
		,#{cbirth}
		,#{apptype}
		,#{appstate}
		,#{cunicode}
		,#{cclass}
		,#{enter}
		,#{graduation}
		,#{pw})

mapper.delete:
delete from tb_apply
where ano = #{ano}
and pw = #{pw}

mapper.update:
    update tb_apply
    set 	pname = #{pname}
			,relation = #{relation}
			,pbirth = #{pbirth}
			,addr = #{addr}
			,phone = #{phone}
			,adddate = sysdate
			,cname = #{cname}
			,cgender = #{cgender}
			,cbirth = #{cbirth}
			,apptype = #{apptype}
			,appstate = #{appstate}
	where ano = #{ano}
	and pw = #{pw}

	 	update tb_apply
	 	set cunicode = #{cunicode}
	 		,cclass = #{cclass}
	 		,enter = to_date(#{enter})
	 		,graduation = to_date(#{graduation})
 		where ano = #{ano}
 		and pw = #{pw}
