mapper.update:
    update tb_apply
    set 	pname = #{pname}
			,relation = #{relation}
			,pbirth = #{pbirth}
			,addr = #{addr}
			,phone = #{phone}
			,cname = #{cname}
			,cgender = #{cgender}
			,cbirth = #{cbirth}
			,apptype = #{apptype}
			,cunicode = #{cunicode}
	where ano = #{ano}

 {call
 	declare
 	begin
		insert into tb_child(cunicode, classname, cname, cbirth)
		values(#{cunicode}, #{cclass}, #{cname}, #{cbirth});

		update tb_apply
		set		cunicode = #{cunicode}
				,cclass = #{cclass}
				,enter = #{enter}
				,graduation = #{graduation}
		where ano = #{ano};
	end
 }
